// components/MemberProfile.js
import React from 'react';
import { Github } from 'lucide-react';

function MemberProfile({ name, role, githubUrl, imageUrl }) {
    return (
        <div className="bg-white rounded-lg shadow-md overflow-hidden hover:shadow-lg transition-shadow duration-300">
            <div className="aspect-square relative">
                <img 
                    src={imageUrl} 
                    alt={`${name}의 프로필`} 
                    className="w-full h-full object-cover"
                />
            </div>
            
            <div className="p-6">
                <h3 className="text-xl font-semibold text-gray-900">{name}</h3>
                <p className="text-gray-600 mt-1">{role}</p>
                
                {githubUrl && (
                    <a 
                        href={githubUrl}
                        target="_blank"
                        rel="noopener noreferrer"
                        className="mt-4 inline-flex items-center gap-2 text-gray-700 hover:text-gray-900 transition-colors"
                    >
                        <Github className="w-5 h-5" />
                        <span>GitHub</span>
                    </a>
                )}
            </div>
        </div>
    );
}

export default MemberProfile;
